(1, '黄彦瑜', '2013-11-25 16:05:06', 'var\r\n  a,b:longint; \r\nbegin\r\n  readln(a,b); \r\n  writeln(a+b); \r\nend. \r\n', 1, 0, '192.168.5.138'),
(2, '黄彦瑜', '2013-11-25 16:24:35', 'var\r\n  n,k,i,j,t,s:longint;\r\n  a:array[1..10000] of longint;\r\n  bo:boolean;\r\nbegin\r\n  readln(n,k);\r\n  for i:=1 to n do\r\n  read(a[i]);\r\n  i:=1;\r\n  repeat\r\n    bo:=true;\r\n    for j:=1 to n-i do\r\n    if a[j]<a[j+1] then\r\n    begin\r\n      t:=a[j];\r\n      a[j]:=a[j+1];\r\n      a[j+1]:=t;\r\n      bo:=false;\r\n    end;\r\n    inc(i);\r\n  until bo;\r\n  s:=a[k]-a[n-k+1];\r\n  if s<2 then begin writeln(''NO''); writeln(s); end\r\n  else\r\n  begin\r\n    for i:=2 to s-1 do\r\n    if s mod i=0 then bo:=false;\r\n    if bo then begin writeln(''YES''); writeln(s); end\r\n    else begin writeln(''NO''); writeln(s); end;\r\n  end;\r\nend.\r\n', 2, 0, '192.168.5.138'),
