(6, '黄彦瑜', '2013-11-28 19:33:10', 'var\r\n  b,c,f,n,i,p,m,k,l,o:longint;\r\n  a:array[1..100] of string;\r\n  s:array[1..100] of longint;\r\n  str,ch,d,e:string;\r\nbegin\r\n  readln(n);\r\n  for i:=1 to n do\r\n  begin\r\n    readln(str);k:=1;\r\n    repeat\r\n      a[i]:=a[i]+str[k];\r\n      inc(k);\r\n    until str[k]='' '';\r\n    l:=k+1;\r\n    repeat\r\n      inc(k);\r\n    until str[k]='' '';\r\n    ch:=copy(str,l,k-l);\r\n    val(ch,b);\r\n    l:=k+1;\r\n    repeat\r\n      inc(k);\r\n    until str[k]='' '';\r\n    ch:=copy(str,l,k-l);\r\n    val(ch,c);\r\n    l:=k+1;\r\n    repeat\r\n      inc(k);\r\n    until str[k]='' '';\r\n    d:=copy(str,l,k-l);\r\n    l:=k+1;\r\n    repeat\r\n      inc(k);\r\n    until str[k]='' '';\r\n     e:=copy(str,l,k-l);\r\n    l:=k+1; k:=k+2;\r\n    ch:=copy(str,l,k-l);\r\n    val(ch,f);\r\n    if (b>80) and (f>0) then s[i]:=s[i]+8000;\r\n    if (b>85) and (c>80) then s[i]:=s[i]+4000;\r\n    if b>90 then s[i]:=s[i]+2000;\r\n    if (b>85) and (e=''Y'') then s[i]:=s[i]+1000;\r\n    if (c>80) and (d=''Y'') then s[i]:=s[i]+850;\r\n    p:=p+s[i]; if s[i]>m then begin m:=s[i]; o:=i; end;\r\n  end;\r\n  writeln(a[o]);\r\n  writeln(m);\r\n  writeln(p);\r\nend.', 6, 0, '192.168.5.146'),
